describe ComponentResultsController do

  def mock_component_result(stubs={})
    @mock_component_result ||= mock_model(ComponentResult, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all component_results as @component_results" do
      ComponentResult.stub(:all) { [mock_component_result] }
      get :index
      assigns(:component_results).should eq([mock_component_result])
    end
  end

  describe "GET show" do
    it "assigns the requested component_result as @component_result" do
      ComponentResult.stub(:find).with("37") { mock_component_result }
      get :show, :id => "37"
      assigns(:component_result).should be(mock_component_result)
    end
  end

  describe "GET new" do
    it "assigns a new component_result as @component_result" do
      ComponentResult.stub(:new) { mock_component_result }
      get :new
      assigns(:component_result).should be(mock_component_result)
    end
  end

  describe "GET edit" do
    it "assigns the requested component_result as @component_result" do
      ComponentResult.stub(:find).with("37") { mock_component_result }
      get :edit, :id => "37"
      assigns(:component_result).should be(mock_component_result)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created component_result as @component_result" do
        ComponentResult.stub(:new).with({'these' => 'params'}) { mock_component_result(:save => true) }
        post :create, :component_result => {'these' => 'params'}
        assigns(:component_result).should be(mock_component_result)
      end

      it "redirects to the created component_result" do
        ComponentResult.stub(:new) { mock_component_result(:save => true) }
        post :create, :component_result => {}
        response.should redirect_to(component_result_url(mock_component_result))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved component_result as @component_result" do
        ComponentResult.stub(:new).with({'these' => 'params'}) { mock_component_result(:save => false) }
        post :create, :component_result => {'these' => 'params'}
        assigns(:component_result).should be(mock_component_result)
      end

      it "re-renders the 'new' template" do
        ComponentResult.stub(:new) { mock_component_result(:save => false) }
        post :create, :component_result => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested component_result" do
        ComponentResult.stub(:find).with("37") { mock_component_result }
        mock_component_result.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :component_result => {'these' => 'params'}
      end

      it "assigns the requested component_result as @component_result" do
        ComponentResult.stub(:find) { mock_component_result(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:component_result).should be(mock_component_result)
      end

      it "redirects to the component_result" do
        ComponentResult.stub(:find) { mock_component_result(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(component_result_url(mock_component_result))
      end
    end

    describe "with invalid params" do
      it "assigns the component_result as @component_result" do
        ComponentResult.stub(:find) { mock_component_result(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:component_result).should be(mock_component_result)
      end

      it "re-renders the 'edit' template" do
        ComponentResult.stub(:find) { mock_component_result(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested component_result" do
      ComponentResult.stub(:find).with("37") { mock_component_result }
      mock_component_result.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the component_results list" do
      ComponentResult.stub(:find) { mock_component_result }
      delete :destroy, :id => "1"
      response.should redirect_to(component_results_url)
    end
  end

